import { useState} from 'react';
import useInput from '../hooks/useInput';



const SimpleInput = (props) => {

const { value: enteredNameVal, 
  isValid: enteredNameIsValid,
  hasError: nameInputHasError, 
  valueHandler: nameChangeHandler,
inputBlurHandler: nameBlurHandler,
reset: resetNameInput
} = useInput(value => value.trim() !== '');


  const [enteredEmail, setEnteredEmail] = useState('');
  const [enteredEmailTouched, setEnteredEmailTouched] = useState(false);



const enteredEmailIsValid = (enteredEmail.trim() !== '') && (enteredEmail.length > 0) && (enteredEmail.includes("@"));
const emailInputIsInvalid = !enteredEmailIsValid && enteredEmailTouched;

let formIsValid = false;


//Check if all inputs are valid, if so, set formIsValid to true.
if (enteredNameIsValid && enteredEmailIsValid) {
  formIsValid = true;
}



//updated the enteredEmail state when user inputs
  const emailInputHandler = event => {
    setEnteredEmail(event.target.value);
    }


    //ON SUBMIT
const onSubmitHandler = (e) => {
e.preventDefault();

//pressing submit button makes every input "touched"
setEnteredNameTouched(true);
setEnteredEmailTouched(true);


//If the entered name and email is invalid, dont submit anything
if (!enteredNameIsValid || !enteredEmailIsValid) {
  return
}


//Restart all states.

setEnteredEmail('');
setEnteredEmailTouched(false);

}



const emailBlurHandler = (e) => {
  setEnteredEmailTouched(true)
}

//toggle classes
const nameInputClasses = nameInputIsInvalid ? 'form-control invalid' : 'form-control';

const emailInputClasses = emailInputIsInvalid ? 'form-control invalid' : 'form-control';

  return (

    <form onSubmit={onSubmitHandler}>


      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input 
        type='text' id='name' 
        onChange={nameChangeHandler}
        onBlur={nameBlurHandler}
        value={enteredNameVal}/>
    {nameInputHasError && <p className="error-text">Name must not be empty</p>}
      </div>

      <div className={emailInputClasses}>
        <label htmlFor='email'>Your Email</label>
        <input 
        type='text' id='email' 
        onChange={emailInputHandler}
        onBlur={emailBlurHandler}
        value={enteredEmail}/>
    {emailInputIsInvalid && <p className="error-text">Invalid Email</p>}
      </div>


      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
