import {useRef, useState} from 'react';



const SimpleInput = (props) => {
  const [enteredName, setEnteredName] = useState('');

  const [enteredNameValid, setEnteredNameValid] = useState(true)
  const nameInputRef = useRef();

const nameInputHandler = event => {
  setEnteredName(event.target.value);
}

const onSubmitHandler = (e) => {
e.preventDefault();

if (enteredName.trim() === "") {
  setEnteredNameValid(false);
  return
}
setEnteredNameValid(true);

}

const nameInputClasses = enteredNameValid ? 'form-control' : 'form-control invalid';

  return (
    <form onSubmit={onSubmitHandler}>
      <div className={nameInputClasses}>
        <label htmlFor='name'>Your Name</label>
        <input ref={nameInputRef} type='text' id='name' onChange={nameInputHandler}/>
    {!enteredNameValid && <p className="error-text">Name must be empty</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
